Method.Implementation:
do ##class(Security.Users).UnExpireUserPasswords("*")
do ##class(Security.Users).AddRoles("admin", "%ALL")
do ##class(Security.System).Get(,.p)
set p("AutheEnabled")=$zb(p("AutheEnabled"),16,7)
set sc = ##class(Security.System).Modify(,.p)
//set sc=##class(Security.Applications).Get("/csp/user",.prop)
//set prop("MatchRoles")=":%All"
//set prop("AutheEnabled")=64
//set sc=##class(Security.Applications).Modify("/csp/user",.prop)
quit sc

set mirrorName="MIRRORSET"
set hostName=$system.INetInfo.HostNameToAddr($system.INetInfo.LocalHostName())
set systemName="MIRRORNODE01"
// Create mirror:
set mirror("UseSSL") = 0
if (ArbiterIP'="none") {
  set mirror("ArbiterNode") = ArbiterIP_"|2188"
  //set mirror("ArbiterPort") = 2188
}
set sc = ##class(SYS.Mirror).CreateNewMirrorSet(mirrorName, systemName, .mirror)
write !,"Creating mirror "_mirrorName_"..."
if 'sc do $system.OBJ.DisplayError(sc)  
quit sc

set dir="/iris/db/" set mirrorName="MIRRORSET"
write !, "Creating databases and NS ",dbName,"...",!
// Create the directory do ##class(%Library.File).CreateNewDir(dir,dbName) // Add DB to config set Properties("Directory")=dir_dbName do ##class(Config.Databases).Create(dbName,.Properties) // Set the DB properties set Properties("Directory")=dir_dbName set rc = ##class(SYS.Database).CreateDatabase(dir_dbName,,,,,,dbName,mirrorName) if 'rc { write !,"Database creation failed!" do $system.OBJ.DisplayError(rc) quit rc }
// Create namespace for mirrored database set ns("Globals")=dbName set ns("Routines")=dbName do ##class(Config.Namespaces).Create(dbName,.ns) set rc = ##class(Config.Namespaces).Exists(dbName,.obj,.status) if 'rc { write !, "NS creation failed." do $system.OBJ.DisplayError(rc) quit rc }
quit $$$OK
